import React, { useState } from "react";
import { Paper, Typography, IconButton, Grid } from "@mui/material";
import { ThumbUp, ThumbDown } from "@mui/icons-material";

const Chatbot = () => {
  // State to track feedback
  const [feedback, setFeedback] = useState(null);

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 p-4">
      <Paper elevation={3} className="p-6 rounded-lg shadow-lg bg-white max-w-lg w-full text-center">
        <Typography variant="h5" className="font-bold text-gray-800 mb-3">
          üí¨ Chatbot
        </Typography>
        <Typography variant="body1" className="text-gray-600">
          How helpful was this response?
        </Typography>

        {/* Feedback Buttons */}
        <Grid container spacing={2} justifyContent="center" className="mt-4">
          <Grid item>
            <IconButton
              onClick={() => setFeedback("up")}
              color={feedback === "up" ? "primary" : "default"}
            >
              <ThumbUp fontSize="large" />
            </IconButton>
          </Grid>
          <Grid item>
            <IconButton
              onClick={() => setFeedback("down")}
              color={feedback === "down" ? "error" : "default"}
            >
              <ThumbDown fontSize="large" />
            </IconButton>
          </Grid>
        </Grid>

        {/* Show feedback message */}
        {feedback && (
          <Typography variant="body2" className="mt-3 text-gray-700">
            {feedback === "up" ? "üëç Thanks for your feedback!" : "üëé We'll try to improve!"}
          </Typography>
        )}
      </Paper>
    </div>
  );
};

export default Chatbot;
