import React, { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleResetRequest = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8000/api/reset-password", { email });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response?.data?.detail || "Error sending reset link.");
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="card p-4 shadow-lg" style={{ width: "350px" }}>
        <h2 className="text-center">Forgot Password</h2>
        <p className="text-center">Enter your email to reset your password.</p>
        <form onSubmit={handleResetRequest}>
          <input 
            type="email" 
            className="form-control mt-3" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button type="submit" className="btn btn-danger w-100 mt-3">Submit</button>
        </form>
        {message && <p className="text-center mt-2 text-success">{message}</p>}
        <Link to="/login" className="d-block text-center mt-2 text-primary">Back to Login</Link>
      </div>
    </div>
  );
};

export default ForgotPassword;
