import React, { useState } from "react";
import { TextField, Button, Typography, Paper, Grid, MenuItem, Select, FormControl, InputLabel, Checkbox, ListItemText } from "@mui/material";
import {  Settings, CloudUpload } from "@mui/icons-material";

const [allowedUsers, setAllowedUsers] = useState([]);
const [newUser, setNewUser] = useState("");




const AdminSettings = () => {
    const [allowedUsers, setAllowedUsers] = useState([]);
    const [newUser, setNewUser] = useState("");

    const handleAddUser = () => {
        if (newUser && !allowedUsers.includes(newUser)) {
        setAllowedUsers([...allowedUsers, newUser]);
        setNewUser("");
        }
    };

    const handleRemoveUser = (userToRemove) => {
        setAllowedUsers(allowedUsers.filter((user) => user !== userToRemove));
    };
  const [logo, setLogo] = useState(null);
  const [bgColor, setBgColor] = useState("#ffffff");
  const [typography, setTypography] = useState("Arial");
  const [fontSize, setFontSize] = useState("16px");
  const [textColor, setTextColor] = useState("#000000");
  const [alignment, setAlignment] = useState("Left");
  const [lineSpacing, setLineSpacing] = useState("1.5");
  const [wordSpacing, setWordSpacing] = useState("Normal");
  const [customAudience, setCustomAudience] = useState([]);
  const [tone, setTone] = useState("Friendly");

  const availableRoles = ["Admin", "Doctor", "Nurse", "Teacher", "Manager", "Student", "HR", "IT Specialist"];

  const handleLogoUpload = (event) => {
    setLogo(event.target.files[0]?.name);
  };

  const handleSaveSettings = () => {
    alert("Settings saved successfully!");
  };

  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh", backgroundColor: "#f0f2f5", padding: "16px" }}>
      <Grid container justifyContent="center" maxWidth="700px">
        <Grid item xs={12}>
          <Paper elevation={3} style={{ padding: "24px", borderRadius: "8px", textAlign: "center" }}>
            <Typography variant="h5" gutterBottom>
              Admin Settings <Settings fontSize="large" />
            </Typography>

            {/* ðŸŸ¡ Stylish Upload Logo Section */}
            <div style={{ backgroundColor: "#eef5ff", padding: "20px", borderRadius: "8px", textAlign: "center", marginBottom: "16px" }}>
              <Typography variant="h6">Upload Logo</Typography>
              <label htmlFor="upload-logo">
                <input type="file" id="upload-logo" style={{ display: "none" }} onChange={handleLogoUpload} />
                <Button variant="contained" color="primary" component="span" startIcon={<CloudUpload />} style={{ marginTop: "10px" }}>
                  Choose File
                </Button>
              </label>
              {logo && <Typography variant="body2" color="textSecondary" style={{ marginTop: "8px" }}>Uploaded Logo: {logo}</Typography>}
            </div>

            {/* ðŸŸ¡ Stylish Background Color Picker */}
            <div style={{ backgroundColor: "#fff3e0", padding: "20px", borderRadius: "8px", textAlign: "center", marginBottom: "16px" }}>
              <Typography variant="h6">Select Background Color</Typography>
              <input type="color" value={bgColor} onChange={(e) => setBgColor(e.target.value)} style={{ width: "50px", height: "50px", border: "none", background: "none" }} />
            </div>

            {/* ðŸŸ¡ Typography Section */}
            <div style={{ backgroundColor: "#f7f7f7", padding: "16px", borderRadius: "8px", marginTop: "16px" }}>
              <Typography variant="h6" gutterBottom>Typography Settings</Typography>

              {/* Font Style */}
              <TextField
                fullWidth
                select
                label="Font Style"
                variant="outlined"
                margin="normal"
                value={typography}
                onChange={(e) => setTypography(e.target.value)}
              >
                <MenuItem value="Arial">Arial</MenuItem>
                <MenuItem value="Times New Roman">Times New Roman</MenuItem>
                <MenuItem value="Verdana">Verdana</MenuItem>
                <MenuItem value="Roboto">Roboto</MenuItem>
                <MenuItem value="Courier New">Courier New</MenuItem>
              </TextField>

              {/* Font Size */}
              <TextField
                fullWidth
                select
                label="Font Size"
                variant="outlined"
                margin="normal"
                value={fontSize}
                onChange={(e) => setFontSize(e.target.value)}
              >
                <MenuItem value="12px">12px</MenuItem>
                <MenuItem value="14px">14px</MenuItem>
                <MenuItem value="16px">16px</MenuItem>
                <MenuItem value="18px">18px</MenuItem>
                <MenuItem value="20px">20px</MenuItem>
              </TextField>

              {/* Text Color */}
              <TextField
                fullWidth
                type="color"
                label="Text Color"
                variant="outlined"
                margin="normal"
                value={textColor}
                onChange={(e) => setTextColor(e.target.value)}
              />

              {/* Alignment */}
              <TextField
                fullWidth
                select
                label="Text Alignment"
                variant="outlined"
                margin="normal"
                value={alignment}
                onChange={(e) => setAlignment(e.target.value)}
              >
                <MenuItem value="Left">Left</MenuItem>
                <MenuItem value="Center">Center</MenuItem>
                <MenuItem value="Right">Right</MenuItem>
                <MenuItem value="Justify">Justify</MenuItem>
              </TextField>

              {/* Line Spacing */}
              <TextField
                fullWidth
                select
                label="Line Spacing"
                variant="outlined"
                margin="normal"
                value={lineSpacing}
                onChange={(e) => setLineSpacing(e.target.value)}
              >
                <MenuItem value="1.0">1.0</MenuItem>
                <MenuItem value="1.5">1.5</MenuItem>
                <MenuItem value="2.0">2.0</MenuItem>
              </TextField>

              {/* Word Spacing */}
              <TextField
                fullWidth
                select
                label="Word Spacing"
                variant="outlined"
                margin="normal"
                value={wordSpacing}
                onChange={(e) => setWordSpacing(e.target.value)}
              >
                <MenuItem value="Normal">Normal</MenuItem>
                <MenuItem value="Wide">Wide</MenuItem>
                <MenuItem value="Extra-Wide">Extra-Wide</MenuItem>
              </TextField>
            </div>

            {/* Custom Audience Selection */}
            <FormControl fullWidth margin="normal">
              <InputLabel>Select Custom Audience</InputLabel>
              <Select
                multiple
                value={customAudience}
                onChange={(e) => setCustomAudience(e.target.value)}
                renderValue={(selected) => selected.join(", ")}
              >
                {availableRoles.map((role) => (
                  <MenuItem key={role} value={role}>
                    <Checkbox checked={customAudience.includes(role)} />
                    <ListItemText primary={role} />
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Tone of Voice Selection */}
            <TextField
              fullWidth
              select
              label="Tone of Voice"
              variant="outlined"
              margin="normal"
              value={tone}
              onChange={(e) => setTone(e.target.value)}
            >
              <MenuItem value="Friendly">Friendly</MenuItem>
              <MenuItem value="Professional">Professional</MenuItem>
              <MenuItem value="Casual">Casual</MenuItem>
              <MenuItem value="Formal">Formal</MenuItem>
            </TextField>

            {/* User Management Section */}
<Paper elevation={3} className="p-6 rounded-lg shadow-lg bg-white w-full mt-6">
  <Typography variant="h6" className="text-center font-bold mb-4 text-gray-800">
    User Management
  </Typography>

  {/* Add User Input */}
  <Grid container spacing={2}>
    <Grid item xs={8}>
      <TextField
        fullWidth
        label="Add User Email"
        variant="outlined"
        size="small"
        value={newUser}
        onChange={(e) => setNewUser(e.target.value)}
      />
    </Grid>
    <Grid item xs={4}>
      <Button
        variant="contained"
        color="primary"
        size="small"
        fullWidth
        onClick={handleAddUser}
      >
        Add User
      </Button>
    </Grid>
  </Grid>

  {/* User List */}
  <List className="mt-4">
    {allowedUsers.map((user, index) => (
      <ListItem
        key={index}
        secondaryAction={
          <IconButton edge="end" color="secondary" onClick={() => handleRemoveUser(user)}>
            <Delete />
          </IconButton>
        }
      >
        <ListItemText primary={user} />
      </ListItem>
    ))}
  </List>
</Paper>


            {/* Save Settings Button */}
            <Button
              variant="contained"
              color="primary"
              fullWidth
              style={{ marginTop: "16px" }}
              onClick={handleSaveSettings}
            >
              Save Settings
            </Button>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};

export default AdminSettings;
