import React, { useState } from "react";
import { TextField, Button, Card, Select, MenuItem, Snackbar, Slider } from "@mui/material";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";

const AdminSettings = () => {
  const [logo, setLogo] = useState(null);
  const [bgColor, setBgColor] = useState("#ffffff");
  const [fontFamily, setFontFamily] = useState("Arial");
  const [fontSize, setFontSize] = useState(16);
  const [fontWeight, setFontWeight] = useState("normal");
  const [textAlign, setTextAlign] = useState("left");
  const [letterSpacing, setLetterSpacing] = useState(0);
  const [lineHeight, setLineHeight] = useState(1.5);
  const [textTransform, setTextTransform] = useState("none");
  const [textDecoration, setTextDecoration] = useState("none");
  const [textColor, setTextColor] = useState("#000000");
  const [tone, setTone] = useState("Friendly");
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  const handleLogoUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setLogo(file.name);
      setSnackbarOpen(true);
    }
  };

  const handleSaveSettings = () => {
    setSnackbarOpen(true);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
    <div className="w-full max-w-lg p-6 bg-white shadow-md rounded-lg">
      <Card className="p-6 shadow-lg w-full max-w-lg">
        <h2 className="text-xl font-bold text-center mb-4">Admin Settings</h2>

        {/* Update Logo */}
        <div className="mt-4">
          <h5 className="text-lg">Update Logo</h5>
          <Button variant="contained" component="label" startIcon={<CloudUploadIcon />}>
            Upload Logo
            <input type="file" hidden onChange={handleLogoUpload} />
          </Button>
          {logo && <p className="text-gray-600 mt-2">Uploaded Logo: {logo}</p>}
        </div>

        {/* Background Color */}
        <div className="mt-4">
          <h5 className="text-lg">Background Color</h5>
          <input type="color" value={bgColor} onChange={(e) => setBgColor(e.target.value)} />
        </div>

        {/* Typography Settings */}
        <div className="mt-4">
          <h5 className="text-lg">Typography</h5>

          {/* Font Family */}
          <Select fullWidth value={fontFamily} onChange={(e) => setFontFamily(e.target.value)}>
            <MenuItem value="Arial">Arial</MenuItem>
            <MenuItem value="Times New Roman">Times New Roman</MenuItem>
            <MenuItem value="Verdana">Verdana</MenuItem>
            <MenuItem value="Courier New">Courier New</MenuItem>
            <MenuItem value="Georgia">Georgia</MenuItem>
            <MenuItem value="Tahoma">Tahoma</MenuItem>
          </Select>

          {/* Font Size */}
          <div className="mt-2">
            <h6>Font Size: {fontSize}px</h6>
            <Slider value={fontSize} min={12} max={48} step={1} onChange={(e, val) => setFontSize(val)} />
          </div>

          {/* Font Weight */}
          <Select fullWidth value={fontWeight} onChange={(e) => setFontWeight(e.target.value)} className="mt-2">
            <MenuItem value="normal">Normal</MenuItem>
            <MenuItem value="bold">Bold</MenuItem>
            <MenuItem value="lighter">Lighter</MenuItem>
          </Select>

          {/* Text Alignment */}
          <Select fullWidth value={textAlign} onChange={(e) => setTextAlign(e.target.value)} className="mt-2">
            <MenuItem value="left">Left</MenuItem>
            <MenuItem value="center">Center</MenuItem>
            <MenuItem value="right">Right</MenuItem>
            <MenuItem value="justify">Justify</MenuItem>
          </Select>

          {/* Letter Spacing */}
          <div className="mt-2">
            <h6>Letter Spacing: {letterSpacing}px</h6>
            <Slider value={letterSpacing} min={0} max={5} step={0.1} onChange={(e, val) => setLetterSpacing(val)} />
          </div>

          {/* Line Height */}
          <div className="mt-2">
            <h6>Line Height: {lineHeight}</h6>
            <Slider value={lineHeight} min={1} max={3} step={0.1} onChange={(e, val) => setLineHeight(val)} />
          </div>

          {/* Text Transform */}
          <Select fullWidth value={textTransform} onChange={(e) => setTextTransform(e.target.value)} className="mt-2">
            <MenuItem value="none">None</MenuItem>
            <MenuItem value="uppercase">Uppercase</MenuItem>
            <MenuItem value="lowercase">Lowercase</MenuItem>
            <MenuItem value="capitalize">Capitalize</MenuItem>
          </Select>

          {/* Text Decoration */}
          <Select fullWidth value={textDecoration} onChange={(e) => setTextDecoration(e.target.value)} className="mt-2">
            <MenuItem value="none">None</MenuItem>
            <MenuItem value="underline">Underline</MenuItem>
            <MenuItem value="line-through">Strikethrough</MenuItem>
          </Select>

          {/* Text Color */}
          <div className="mt-2">
            <h6>Text Color</h6>
            <input type="color" value={textColor} onChange={(e) => setTextColor(e.target.value)} />
          </div>
        </div>

        {/* Tone of Voice */}
        <div className="mt-4">
          <h5 className="text-lg">Tone of Voice</h5>
          <Select fullWidth value={tone} onChange={(e) => setTone(e.target.value)}>
            <MenuItem value="Friendly">Friendly</MenuItem>
            <MenuItem value="Professional">Professional</MenuItem>
            <MenuItem value="Casual">Casual</MenuItem>
          </Select>
        </div>

        {/* Save Button */}
        <Button className="mt-4 w-full" variant="contained" color="primary" onClick={handleSaveSettings}>
          Save Settings
        </Button>

        {/* Success Snackbar */}
        <Snackbar
          open={snackbarOpen}
          autoHideDuration={3000}
          onClose={() => setSnackbarOpen(false)}
          message="Settings saved successfully!"
        />
      </Card>
    </div>
    </div>
  );
};

export default AdminSettings;
