import React, { useState } from "react";
import { IconButton, Paper, Typography } from "@mui/material";
import { ChatBubbleOutline, Close } from "@mui/icons-material";

const DigiBot = () => {
  const [open, setOpen] = useState(false);

  return (
    <div>
      {/* Floating Chatbot Icon */}
      {!open && (
        <IconButton
          onClick={() => setOpen(true)}
          style={{
            position: "fixed",
            bottom: 20,
            right: 20,
            backgroundColor: "#007bff",
            color: "white",
            padding: "12px",
            borderRadius: "50px",
            boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
          }}
        >
          <ChatBubbleOutline fontSize="large" />
        </IconButton>
      )}

      {/* Chatbot Popup Window */}
      {open && (
        <Paper
          elevation={6}
          style={{
            position: "fixed",
            bottom: 20,
            right: 20,
            width: "320px",
            height: "400px",
            borderRadius: "12px",
            overflow: "hidden",
            display: "flex",
            flexDirection: "column",
            boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.2)",
          }}
        >
          {/* Header */}
          <div style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            padding: "10px",
            backgroundColor: "#007bff",
            color: "white",
          }}>
            <Typography variant="h6">DigiBot</Typography>
            <IconButton onClick={() => setOpen(false)} style={{ color: "white" }}>
              <Close />
            </IconButton>
          </div>

          {/* Chat Content */}
          <div style={{ flex: 1, padding: "10px", overflowY: "auto", backgroundColor: "#f8f9fa" }}>
            <Typography variant="body2" color="textSecondary">Chatbot conversation will appear here...</Typography>
          </div>

          {/* Footer */}
          <div style={{ padding: "10px", backgroundColor: "white", borderTop: "1px solid #ccc" }}>
            <Typography variant="body2" align="center">Powered by DigiBot</Typography>
          </div>
        </Paper>
      )}
    </div>
  );
};

export default DigiBot;
