import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import Login from "./Pages/Login"; // ✅ Import the Login (AuthPage) correctly
import Authentication from "./Pages/Authentication";
import Feedback from "./Pages/Feedback";
import ForgotPassword from "./Pages/ForgotPassword";
import Chatbot from "./Pages/Chatbot";
import AdminSettings from "./Pages/AdminSettings"; 
import SSOAuthentication from "./Pages/SSOAuthentication";

// Dummy role check (Replace with real authentication logic)
const getUserRole = () => {
  return localStorage.getItem("userRole") || "user"; // Default role is "user"
};

// ✅ Ensure ProtectedRoute is a function, NOT an object!
const ProtectedRoute = ({ element, adminOnly }) => {
  const role = getUserRole();
  if (adminOnly && role !== "admin") {
    return <Navigate to="/chatbot" />;
  }
  return element;
};

const App = () => {
  return (
    <Router>
      <div className="min-h-screen flex flex-col items-center bg-gray-100 p-4">
        <nav className="w-full max-w-3xl flex justify-between bg-white p-4 rounded shadow">
          <Link to="/login" className="text-blue-600">Login</Link>
          <Link to="/authentication" className="text-blue-600">Authentication</Link>
          <Link to="/feedback" className="text-blue-600">Feedback</Link>
          <Link to="/chatbot" className="text-blue-600">Chatbot</Link>
          <Link to="/admin-settings" className="text-blue-600">Admin Settings</Link>
          <Link to="/sso-authentication" className="text-blue-600">SSO Authentication</Link>
        </nav>

        <div className="w-full max-w-3xl bg-white p-6 mt-6 rounded shadow">
          <Routes>
            <Route path="/login" element={<Login />} /> 
            <Route path="/authentication" element={<Authentication />} />
            <Route path="/feedback" element={<Feedback />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/chatbot" element={<Chatbot />} />
            <Route path="/admin-settings" element={<ProtectedRoute element={<AdminSettings />} adminOnly={true} />} />
            <Route path="/sso-authentication" element={<SSOAuthentication />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
