import React, { useState } from "react";
import { TextField, Button, Typography, Paper, Grid, MenuItem, InputAdornment, IconButton } from "@mui/material";
import { Upload, Settings, FormatBold, FormatItalic, FormatUnderlined } from "@mui/icons-material";

const AdminSettings = () => {
  const [logo, setLogo] = useState(null);
  const [bgColor, setBgColor] = useState("#ffffff");
  const [typography, setTypography] = useState("Arial");
  const [fontSize, setFontSize] = useState("16px");
  const [textColor, setTextColor] = useState("#000000");
  const [alignment, setAlignment] = useState("Left");
  const [letterSpacing, setLetterSpacing] = useState("0");
  const [wordSpacing, setWordSpacing] = useState("1");
  const [lineSpacing, setLineSpacing] = useState("1.5");
  const [textDecoration, setTextDecoration] = useState("None");
  const [textTransform, setTextTransform] = useState("None");
  const [allowedUsers, setAllowedUsers] = useState([]);
  const [newUser, setNewUser] = useState("");

  const handleLogoUpload = (event) => {
    setLogo(event.target.files[0]?.name);
  };

  const handleSaveSettings = () => {
    alert("Settings saved successfully!");
  };

  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh", backgroundColor: "#f0f2f5", padding: "16px" }}>
      <Grid container justifyContent="center" maxWidth="700px">
        <Grid item xs={12}>
          <Paper elevation={3} style={{ padding: "24px", borderRadius: "8px", textAlign: "center" }}>
            <Typography variant="h5" gutterBottom>
              Admin Settings <Settings fontSize="large" />
            </Typography>

            {/* Logo Upload */}
            <TextField
              fullWidth
              type="file"
              onChange={handleLogoUpload}
              variant="outlined"
              margin="normal"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <IconButton component="label">
                      <Upload />
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
            {logo && <Typography variant="body2" color="textSecondary">Uploaded Logo: {logo}</Typography>}

            {/* Background Color */}
            <TextField
              fullWidth
              type="color"
              label="Background Color"
              variant="outlined"
              margin="normal"
              value={bgColor}
              onChange={(e) => setBgColor(e.target.value)}
            />

            {/* Typography Settings */}
            <Typography variant="h6" gutterBottom>Typography Settings</Typography>

            {/* Font Style */}
            <TextField
              fullWidth
              select
              label="Font Style"
              variant="outlined"
              margin="normal"
              value={typography}
              onChange={(e) => setTypography(e.target.value)}
            >
              <MenuItem value="Arial">Arial</MenuItem>
              <MenuItem value="Times New Roman">Times New Roman</MenuItem>
              <MenuItem value="Verdana">Verdana</MenuItem>
              <MenuItem value="Roboto">Roboto</MenuItem>
              <MenuItem value="Courier New">Courier New</MenuItem>
            </TextField>

            {/* Font Size */}
            <TextField
              fullWidth
              select
              label="Font Size"
              variant="outlined"
              margin="normal"
              value={fontSize}
              onChange={(e) => setFontSize(e.target.value)}
            >
              <MenuItem value="12px">12px</MenuItem>
              <MenuItem value="14px">14px</MenuItem>
              <MenuItem value="16px">16px</MenuItem>
              <MenuItem value="18px">18px</MenuItem>
              <MenuItem value="20px">20px</MenuItem>
            </TextField>

            {/* Text Color */}
            <TextField
              fullWidth
              type="color"
              label="Text Color"
              variant="outlined"
              margin="normal"
              value={textColor}
              onChange={(e) => setTextColor(e.target.value)}
            />

            {/* Text Alignment */}
            <TextField
              fullWidth
              select
              label="Text Alignment"
              variant="outlined"
              margin="normal"
              value={alignment}
              onChange={(e) => setAlignment(e.target.value)}
            >
              <MenuItem value="Left">Left</MenuItem>
              <MenuItem value="Center">Center</MenuItem>
              <MenuItem value="Right">Right</MenuItem>
              <MenuItem value="Justify">Justify</MenuItem>
            </TextField>

            {/* Letter Spacing */}
            <TextField
              fullWidth
              label="Letter Spacing"
              variant="outlined"
              margin="normal"
              value={letterSpacing}
              onChange={(e) => setLetterSpacing(e.target.value)}
            />

            {/* Word Spacing */}
            <TextField
              fullWidth
              label="Word Spacing"
              variant="outlined"
              margin="normal"
              value={wordSpacing}
              onChange={(e) => setWordSpacing(e.target.value)}
            />

            {/* Line Spacing */}
            <TextField
              fullWidth
              select
              label="Line Spacing"
              variant="outlined"
              margin="normal"
              value={lineSpacing}
              onChange={(e) => setLineSpacing(e.target.value)}
            >
              <MenuItem value="1">1</MenuItem>
              <MenuItem value="1.5">1.5</MenuItem>
              <MenuItem value="2">2</MenuItem>
              <MenuItem value="2.5">2.5</MenuItem>
            </TextField>

            {/* Text Decoration */}
            <TextField
              fullWidth
              select
              label="Text Decoration"
              variant="outlined"
              margin="normal"
              value={textDecoration}
              onChange={(e) => setTextDecoration(e.target.value)}
            >
              <MenuItem value="None">None</MenuItem>
              <MenuItem value="Bold"><FormatBold /> Bold</MenuItem>
              <MenuItem value="Italic"><FormatItalic /> Italic</MenuItem>
              <MenuItem value="Underline"><FormatUnderlined /> Underline</MenuItem>
            </TextField>

            {/* Save Settings Button */}
            <Button
              variant="contained"
              color="primary"
              fullWidth
              style={{ marginTop: "16px" }}
              onClick={handleSaveSettings}
            >
              Save Settings
            </Button>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};

export default AdminSettings;
