import React, { useState } from "react";
import { TextField, Button, Typography, Paper, Grid, MenuItem, Select, InputAdornment, IconButton, FormControl, InputLabel, Checkbox, ListItemText } from "@mui/material";
import { Upload, Settings, FormatBold, FormatItalic, FormatUnderlined } from "@mui/icons-material";

const AdminSettings = () => {
  const [logo, setLogo] = useState(null);
  const [bgColor, setBgColor] = useState("#ffffff");
  const [typography, setTypography] = useState("Arial");
  const [fontSize, setFontSize] = useState("16px");
  const [textColor, setTextColor] = useState("#000000");
  const [alignment, setAlignment] = useState("Left");
  const [letterSpacing, setLetterSpacing] = useState("0");
  const [wordSpacing, setWordSpacing] = useState("1");
  const [lineSpacing, setLineSpacing] = useState("1.5");
  const [textDecoration, setTextDecoration] = useState("None");
  const [textTransform, setTextTransform] = useState("None");
  const [allowedUsers, setAllowedUsers] = useState([]);
  const [newUser, setNewUser] = useState("");
  const [tone, setTone] = useState("Friendly");

  const [customAudience, setCustomAudience] = useState([]);

  const availableRoles = ["Admin", "Doctor", "Nurse", "Teacher", "Manager", "Student", "HR", "IT Specialist"];

  const handleLogoUpload = (event) => {
    setLogo(event.target.files[0]?.name);
  };

  const handleSaveSettings = () => {
    alert("Settings saved successfully!");
  };

  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh", backgroundColor: "#f0f2f5", padding: "16px" }}>
      <Grid container justifyContent="center" maxWidth="700px">
        <Grid item xs={12}>
          <Paper elevation={3} style={{ padding: "24px", borderRadius: "8px", textAlign: "center" }}>
            <Typography variant="h5" gutterBottom>
              Admin Settings <Settings fontSize="large" />
            </Typography>

            {/* Logo Upload */}
            <TextField
              fullWidth
              type="file"
              onChange={handleLogoUpload}
              variant="outlined"
              margin="normal"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <IconButton component="label">
                      <Upload />
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
            {logo && <Typography variant="body2" color="textSecondary">Uploaded Logo: {logo}</Typography>}

            {/* Background Color */}
            <TextField
              fullWidth
              type="color"
              label="Background Color"
              variant="outlined"
              margin="normal"
              value={bgColor}
              onChange={(e) => setBgColor(e.target.value)}
            />

            {/* Typography Settings */}
            <Typography variant="h6" gutterBottom>Typography Settings</Typography>

            {/* Font Style */}
            <TextField
              fullWidth
              select
              label="Font Style"
              variant="outlined"
              margin="normal"
              value={typography}
              onChange={(e) => setTypography(e.target.value)}
            >
              <MenuItem value="Arial">Arial</MenuItem>
              <MenuItem value="Times New Roman">Times New Roman</MenuItem>
              <MenuItem value="Verdana">Verdana</MenuItem>
              <MenuItem value="Roboto">Roboto</MenuItem>
              <MenuItem value="Courier New">Courier New</MenuItem>
            </TextField>

            {/* Font Size */}
            <TextField
              fullWidth
              select
              label="Font Size"
              variant="outlined"
              margin="normal"
              value={fontSize}
              onChange={(e) => setFontSize(e.target.value)}
            >
              <MenuItem value="12px">12px</MenuItem>
              <MenuItem value="14px">14px</MenuItem>
              <MenuItem value="16px">16px</MenuItem>
              <MenuItem value="18px">18px</MenuItem>
              <MenuItem value="20px">20px</MenuItem>
            </TextField>

            {/* Text Color */}
            <TextField
              fullWidth
              type="color"
              label="Text Color"
              variant="outlined"
              margin="normal"
              value={textColor}
              onChange={(e) => setTextColor(e.target.value)}
            />

            {/* Custom Audience Selection */}
            <FormControl fullWidth margin="normal">
              <InputLabel>Select Custom Audience</InputLabel>
              <Select
                multiple
                value={customAudience}
                onChange={(e) => setCustomAudience(e.target.value)}
                renderValue={(selected) => selected.join(", ")}
              >
                {availableRoles.map((role) => (
                  <MenuItem key={role} value={role}>
                    <Checkbox checked={customAudience.includes(role)} />
                    <ListItemText primary={role} />
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Tone of Voice Selection */}
            <TextField
              fullWidth
              select
              label="Tone of Voice"
              variant="outlined"
              margin="normal"
              value={tone}
              onChange={(e) => setTone(e.target.value)}
            >
              <MenuItem value="Friendly">Friendly</MenuItem>
              <MenuItem value="Professional">Professional</MenuItem>
              <MenuItem value="Casual">Casual</MenuItem>
              <MenuItem value="Formal">Formal</MenuItem>
            </TextField>

            {/* Save Settings Button */}
            <Button
              variant="contained"
              color="primary"
              fullWidth
              style={{ marginTop: "16px" }}
              onClick={handleSaveSettings}
            >
              Save Settings
            </Button>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};

export default AdminSettings;
